import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/ui/Card';
import Button from '../../components/ui/Button';
import Input from '../../components/ui/Input';
import Modal from '../../components/ui/Modal';
import Badge from '../../components/ui/Badge';
import { jobsAPI } from '../../services/api';
import toast from 'react-hot-toast';
import {
  Briefcase,
  Plus,
  Search,
  Filter,
  Eye,
  Edit,
  Trash2,
  Calendar,
  MapPin,
  DollarSign,
  Users,
  Loader2,
} from 'lucide-react';

const JobsPage = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedJob, setSelectedJob] = useState(null);
  const [isDetailModalOpen, setIsDetailModalOpen] = useState(false);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);

  // Fetch jobs from API
  useEffect(() => {
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      setLoading(true);
      const response = await jobsAPI.getAll();
      if (response.data.success) {
        setJobs(response.data.data.jobs);
      }
    } catch (error) {
      console.error('Error fetching jobs:', error);
      toast.error('Failed to fetch jobs');
    } finally {
      setLoading(false);
    }
  };

  const filteredJobs = jobs.filter(
    (job) =>
      job.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      job.country.toLowerCase().includes(searchTerm.toLowerCase()) ||
      job.positions?.some(pos => pos.title?.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  const handleViewDetails = (job) => {
    setSelectedJob(job);
    setIsDetailModalOpen(true);
  };

  const getStatusBadge = (status) => {
    const variants = {
      Active: 'success',
      Inactive: 'secondary',
      Draft: 'warning',
    };
    return <Badge variant={variants[status] || 'default'}>{status}</Badge>;
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Jobs</h1>
          <p className="text-gray-600">
            Manage job listings and track applications.
          </p>
        </div>
        <Button onClick={() => setIsAddModalOpen(true)}>
          <Plus className="mr-2 h-4 w-4" />
          Add Job
        </Button>
      </div>

      {/* Search and Filters */}
      <Card>
        <CardContent className="p-6">
          <div className="flex flex-col sm:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <Input
                  placeholder="Search jobs by name, country, or position..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>
            <Button variant="outline">
              <Filter className="mr-2 h-4 w-4" />
              Filters
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Jobs Grid */}
      {loading ? (
        <div className="flex items-center justify-center py-12">
          <Loader2 className="h-8 w-8 animate-spin text-primary-600" />
          <span className="ml-2 text-gray-600">Loading jobs...</span>
        </div>
      ) : filteredJobs.length === 0 ? (
        <div className="text-center py-12">
          <Briefcase className="h-12 w-12 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">No jobs found</h3>
          <p className="text-gray-600">
            {searchTerm ? 'Try adjusting your search terms.' : 'Get started by adding your first job listing.'}
          </p>
        </div>
      ) : (
        <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {filteredJobs.map((job) => (
            <Card key={job.id} className="hover:shadow-lg transition-shadow">
              <CardHeader>
                <div className="flex justify-between items-start">
                  <div>
                    <CardTitle className="text-lg">{job.name}</CardTitle>
                    <div className="flex items-center mt-2 text-sm text-gray-500">
                      <MapPin className="h-4 w-4 mr-1" />
                      {job.city}, {job.country}
                    </div>
                  </div>
                  {getStatusBadge(job.status)}
                </div>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {/* Positions */}
                  <div>
                    <label className="text-sm font-medium text-gray-700">Positions:</label>
                    <div className="flex flex-wrap gap-1 mt-1">
                      {job.positions?.map((position, index) => (
                        <Badge key={index} variant="outline" size="sm">
                          {position.title}
                        </Badge>
                      ))}
                    </div>
                  </div>

                  {/* Fee */}
                  <div className="flex items-center text-sm text-gray-600">
                    <DollarSign className="h-4 w-4 mr-1" />
                    Fee: {job.application_fee === 0 ? 'Free' : `$${job.application_fee}`}
                  </div>

                  {/* Deadline */}
                  <div className="flex items-center text-sm text-gray-600">
                    <Calendar className="h-4 w-4 mr-1" />
                    Deadline: {new Date(job.deadline).toLocaleDateString()}
                  </div>

                  {/* Applications */}
                  <div className="flex items-center text-sm text-gray-600">
                    <Users className="h-4 w-4 mr-1" />
                    {job.application_count || 0} applications
                  </div>

                  {/* Actions */}
                  <div className="flex space-x-2 pt-3 border-t">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleViewDetails(job)}
                      className="flex-1"
                    >
                      <Eye className="h-4 w-4 mr-1" />
                      Details
                    </Button>
                    <Button variant="outline" size="sm">
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button variant="outline" size="sm">
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {/* Job Detail Modal */}
      <Modal
        isOpen={isDetailModalOpen}
        onClose={() => setIsDetailModalOpen(false)}
        title={selectedJob?.name}
        size="lg"
      >
        {selectedJob && (
          <div className="space-y-6">
            {/* Job Info */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Job Name
                </label>
                <p className="mt-1 text-sm text-gray-900">{selectedJob.name}</p>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Location
                </label>
                <p className="mt-1 text-sm text-gray-900">{selectedJob.city}, {selectedJob.country}</p>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Fee
                </label>
                <p className="mt-1 text-sm text-gray-900">
                  {selectedJob.application_fee === 0 ? 'Free' : `$${selectedJob.application_fee}`}
                </p>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Status
                </label>
                <div className="mt-1">{getStatusBadge(selectedJob.status)}</div>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Deadline
                </label>
                <p className="mt-1 text-sm text-gray-900">
                  {new Date(selectedJob.deadline).toLocaleDateString()}
                </p>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Applications
                </label>
                <p className="mt-1 text-sm text-gray-900">
                  {selectedJob.application_count || 0} applications
                </p>
              </div>
            </div>

            {/* Positions */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Available Positions
              </label>
              <div className="flex flex-wrap gap-2">
                {selectedJob.positions?.map((position, index) => (
                  <Badge key={index} variant="primary">
                    {position.title}
                  </Badge>
                ))}
              </div>
            </div>

            {/* Description */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Description
              </label>
              <p className="text-sm text-gray-900 bg-gray-50 p-3 rounded-md">
                {selectedJob.description}
              </p>
            </div>

            {/* Requirements */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Requirements
              </label>
              <div className="bg-gray-50 p-4 rounded-md space-y-3">
                <div>
                  <span className="font-medium text-gray-700">Requirements: </span>
                  <span className="text-gray-900">{selectedJob.requirements}</span>
                </div>
                <div>
                  <span className="font-medium text-gray-700">Qualifications: </span>
                  <span className="text-gray-900">{selectedJob.qualifications}</span>
                </div>
                <div>
                  <span className="font-medium text-gray-700">Tags: </span>
                  <div className="flex flex-wrap gap-1 mt-1">
                    {selectedJob.tags?.map((tag, index) => (
                      <Badge key={index} variant="outline" size="sm">
                        {tag}
                      </Badge>
                    ))}
                  </div>
                </div>
              </div>
            </div>

            {/* Actions */}
            <div className="flex justify-end space-x-3">
              <Button variant="outline" onClick={() => setIsDetailModalOpen(false)}>
                Close
              </Button>
              <Button>
                <Edit className="h-4 w-4 mr-2" />
                Edit Job
              </Button>
            </div>
          </div>
        )}
      </Modal>

      {/* Add Job Modal - Placeholder */}
      <Modal
        isOpen={isAddModalOpen}
        onClose={() => setIsAddModalOpen(false)}
        title="Add New Job"
        size="lg"
      >
        <div className="space-y-4">
          <p className="text-gray-600">
            Add job functionality would be implemented here with a form containing:
          </p>
          <ul className="list-disc list-inside text-sm text-gray-600 space-y-1">
            <li>Job Name</li>
            <li>Country</li>
            <li>Positions (multiple selection)</li>
            <li>Fee</li>
            <li>Status</li>
            <li>Deadline</li>
            <li>Description</li>
            <li>Requirements</li>
          </ul>
          <div className="flex justify-end space-x-3">
            <Button variant="outline" onClick={() => setIsAddModalOpen(false)}>
              Cancel
            </Button>
            <Button onClick={() => setIsAddModalOpen(false)}>
              Save Job
            </Button>
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default JobsPage;
