import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { QueryClient, QueryClientProvider } from 'react-query';
import { Toaster } from 'react-hot-toast';

// Auth Context
import { AuthProvider, useAuth } from './contexts/AuthContext';

// Layout Components
import DashboardLayout from './components/layout/DashboardLayout';
import AuthLayout from './components/layout/AuthLayout';

// Auth Pages
import Login from './pages/auth/Login';
import Register from './pages/auth/Register';
import ForgotPassword from './pages/auth/ForgotPassword';
import ResetPassword from './pages/auth/ResetPassword';

// Admin/Staff Pages
import AdminDashboard from './pages/admin/Dashboard';
import ApplicantsPage from './pages/admin/ApplicantsPage';
import JobsPage from './pages/admin/JobsPage';
import ScholarshipsPage from './pages/admin/ScholarshipsPage';
import ChatPage from './pages/admin/ChatPage';
import StaffManagementPage from './pages/admin/StaffManagementPage';
import AdsPage from './pages/admin/AdsPage';

// Applicant Pages
import ApplicantDashboard from './pages/applicant/Dashboard';
import ProfilePage from './pages/applicant/ProfilePage';
import ApplicationsPage from './pages/applicant/ApplicationsPage';
import ApplicantJobsPage from './pages/applicant/JobsPage';
import ApplicantScholarshipsPage from './pages/applicant/ScholarshipsPage';
import ApplicantChatPage from './pages/applicant/ChatPage';

// Wizard Pages
import ApplicationWizard from './pages/wizard/ApplicationWizard';

// Error Pages
import NotFound from './pages/error/NotFound';

// Create a client
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      retry: 1,
      refetchOnWindowFocus: false,
    },
  },
});

// Protected Route Component
const ProtectedRoute = ({ children, allowedRoles = [] }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  if (allowedRoles.length > 0 && !allowedRoles.includes(user.role)) {
    return <Navigate to="/unauthorized" replace />;
  }

  return children;
};

// Role-based Route Component
const RoleBasedRoute = ({ adminComponent, staffComponent, applicantComponent }) => {
  const { user } = useAuth();

  switch (user?.role) {
    case 'admin':
      return adminComponent;
    case 'staff':
      return staffComponent;
    case 'applicant':
      return applicantComponent;
    default:
      return <Navigate to="/login" replace />;
  }
};

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <AuthProvider>
        <Router>
          <div className="App">
            <Toaster 
              position="top-right"
              toastOptions={{
                duration: 4000,
                style: {
                  background: '#363636',
                  color: '#fff',
                },
                success: {
                  duration: 3000,
                  iconTheme: {
                    primary: '#22c55e',
                    secondary: '#fff',
                  },
                },
                error: {
                  duration: 5000,
                  iconTheme: {
                    primary: '#ef4444',
                    secondary: '#fff',
                  },
                },
              }}
            />

            <Routes>
              {/* Public Routes */}
              <Route path="/login" element={
                <AuthLayout>
                  <Login />
                </AuthLayout>
              } />
              <Route path="/register" element={
                <AuthLayout>
                  <Register />
                </AuthLayout>
              } />
              <Route path="/forgot-password" element={
                <AuthLayout>
                  <ForgotPassword />
                </AuthLayout>
              } />
              <Route path="/reset-password" element={
                <AuthLayout>
                  <ResetPassword />
                </AuthLayout>
              } />

              {/* Protected Dashboard Routes */}
              <Route path="/dashboard" element={
                <ProtectedRoute>
                  <DashboardLayout>
                    <RoleBasedRoute
                      adminComponent={<AdminDashboard />}
                      staffComponent={<AdminDashboard />}
                      applicantComponent={<ApplicantDashboard />}
                    />
                  </DashboardLayout>
                </ProtectedRoute>
              } />

              {/* Admin/Staff Routes */}
              <Route path="/admin/applicants" element={
                <ProtectedRoute allowedRoles={['admin', 'staff']}>
                  <DashboardLayout>
                    <ApplicantsPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />
              <Route path="/admin/jobs" element={
                <ProtectedRoute allowedRoles={['admin', 'staff']}>
                  <DashboardLayout>
                    <JobsPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />
              <Route path="/admin/scholarships" element={
                <ProtectedRoute allowedRoles={['admin', 'staff']}>
                  <DashboardLayout>
                    <ScholarshipsPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />
              <Route path="/admin/chat" element={
                <ProtectedRoute allowedRoles={['admin', 'staff']}>
                  <DashboardLayout>
                    <ChatPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />
              <Route path="/admin/staff" element={
                <ProtectedRoute allowedRoles={['admin']}>
                  <DashboardLayout>
                    <StaffManagementPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />
              <Route path="/admin/ads" element={
                <ProtectedRoute allowedRoles={['admin']}>
                  <DashboardLayout>
                    <AdsPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />

              {/* Applicant Routes */}
              <Route path="/profile" element={
                <ProtectedRoute allowedRoles={['applicant']}>
                  <DashboardLayout>
                    <ProfilePage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />
              <Route path="/applications" element={
                <ProtectedRoute allowedRoles={['applicant']}>
                  <DashboardLayout>
                    <ApplicationsPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />
              <Route path="/jobs" element={
                <ProtectedRoute allowedRoles={['applicant']}>
                  <DashboardLayout>
                    <ApplicantJobsPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />
              <Route path="/scholarships" element={
                <ProtectedRoute allowedRoles={['applicant']}>
                  <DashboardLayout>
                    <ApplicantScholarshipsPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />
              <Route path="/chat" element={
                <ProtectedRoute allowedRoles={['applicant']}>
                  <DashboardLayout>
                    <ApplicantChatPage />
                  </DashboardLayout>
                </ProtectedRoute>
              } />

              {/* Wizard Routes */}
              <Route path="/wizard/:type" element={
                <ProtectedRoute allowedRoles={['applicant']}>
                  <DashboardLayout>
                    <ApplicationWizard />
                  </DashboardLayout>
                </ProtectedRoute>
              } />

              {/* Default Routes */}
              <Route path="/" element={<Navigate to="/dashboard" replace />} />
              <Route path="/unauthorized" element={
                <div className="min-h-screen flex items-center justify-center">
                  <div className="text-center">
                    <h1 className="text-2xl font-bold text-gray-900 mb-4">Unauthorized Access</h1>
                    <p className="text-gray-600 mb-8">You don't have permission to access this page.</p>
                    <button 
                      onClick={() => window.history.back()}
                      className="btn btn-primary"
                    >
                      Go Back
                    </button>
                  </div>
                </div>
              } />
              <Route path="*" element={<NotFound />} />
            </Routes>
          </div>
        </Router>
      </AuthProvider>
    </QueryClientProvider>
  );
}

export default App;
