import Badge from '../../components/ui/Badge';
import {
  MessageSquare,
  Send,
  Paperclip,
  Smile,
  Search,
  Phone,
  Video,
  MoreVertical,
  Circle,
  Clock,
  Check,
  CheckCheck,
} from 'lucide-react';

