        email: 'jane.smith@example.com',
        role: 'applicant',
        avatar: '/api/placeholder/40/40',
        isOnline: false,
        lastSeen: '2024-01-20T09:15:00Z',
      },
      lastMessage: {
        content: 'I have uploaded all the required documents.',
        timestamp: '2024-01-20T09:15:00Z',
        isFromCurrentUser: false,
        status: 'delivered',
      },
      unreadCount: 0,
      updatedAt: '2024-01-20T09:15:00Z',
    },
    {
      id: 3,
      participant: {
        id: 3,
        name: 'Mike Johnson',
        email: 'mike.johnson@example.com',
        role: 'applicant',
        avatar: '/api/placeholder/40/40',
        isOnline: true,
        lastSeen: null,
      },
      lastMessage: {
        content: 'Could you please review my application?',
