import React, { useEffect, useRef, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { gsap } from 'gsap';

const Header = () => {
  const [mobileOpen, setMobileOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const onScroll = () => setScrolled(window.scrollY > 100);
    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  const handleApply = () => {
    const token = localStorage.getItem('token');
    if (token) navigate('/dashboard'); else navigate('/auth/register');
  };

  const handleLogin = () => navigate('/auth/login');

  const scrollToId = (id) => {
    const el = document.getElementById(id);
    if (!el) return;
    const y = el.getBoundingClientRect().top + window.scrollY - 72;
    window.scrollTo({ top: y, behavior: 'smooth' });
    setMobileOpen(false);
  };

  const navLink = (id, label) => (
    <button onClick={() => scrollToId(id)} className="px-3 py-2 text-sm font-medium text-gray-700 hover:text-gray-900">
      {label}
    </button>
  );

  return (
    <header className={`fixed top-0 inset-x-0 z-50 ${scrolled ? 'bg-white/80 backdrop-blur-md shadow-sm' : 'bg-white/60 backdrop-blur-md border-b'} transition`}> 
      <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8 h-16 flex items-center justify-between">
        <div className="flex items-center gap-2">
          <div className="w-9 h-9 rounded-full" style={{ background: 'var(--gradient-primary)' }} />
          <span className="font-semibold">Addis Consult Solutions</span>
        </div>
        <nav className="hidden md:flex items-center gap-1">
          {navLink('home', 'Home')}
          {navLink('services', 'Services')}
          {navLink('about', 'About')}
          {navLink('success', 'Success Stories')}
          {navLink('partners', 'Partners')}
          {navLink('faq', 'FAQ')}
        </nav>
        <div className="hidden md:flex items-center gap-2">
          <div className="rounded-full border px-1 py-1 text-xs">
            <button className="px-2">EN</button>
            <button className="px-2">አማ</button>
            <button className="px-2">OM</button>
          </div>
          <button onClick={handleLogin} className="btn btn-outline">Login</button>
          <button onClick={handleApply} className="btn btn-primary-gradient">Apply Now</button>
        </div>
        <button aria-label="Menu" className="md:hidden p-2" onClick={() => setMobileOpen(!mobileOpen)}>
          <i className="fas fa-bars text-xl"></i>
        </button>
      </div>
      {mobileOpen && (
        <div className="md:hidden fixed inset-0 bg-white z-40 pt-16">
          <div className="p-6 space-y-4">
            {['home','services','about','success','partners','faq'].map(id => (
              <button key={id} onClick={() => scrollToId(id)} className="block w-full text-left text-lg py-3 border-b">
                {id === 'home' ? 'Home' : id.charAt(0).toUpperCase()+id.slice(1)}
              </button>
            ))}
            <div className="pt-4 flex gap-3">
              <button onClick={handleLogin} className="btn btn-outline flex-1">Login</button>
              <button onClick={handleApply} className="btn btn-primary-gradient flex-1">Apply Now</button>
            </div>
          </div>
        </div>
      )}
    </header>
  );
};

const Hero = () => {
  const heroRef = useRef(null);
  const globeRef = useRef(null);
  const floatRefs = useRef([]);
  floatRefs.current = [];

  const addFloatRef = (el) => {
    if (el && !floatRefs.current.includes(el)) floatRefs.current.push(el);
  };

  // Remove entrance animations on load; only continuous motions
  useEffect(() => {
    if (globeRef.current) {
      gsap.to(globeRef.current, { rotate: 360, duration: 60, ease: 'none', repeat: -1 });
    }
    floatRefs.current.forEach((el, idx) => {
      gsap.to(el, { y: 12, rotation: idx % 2 ? 6 : -6, duration: 3 + (idx % 3), yoyo: true, repeat: -1, ease: 'sine.inOut' });
    });

    const onScroll = () => {
      const y = window.scrollY || 0;
      if (heroRef.current) {
        const parallax = Math.min(y * 0.1, 40);
        heroRef.current.style.setProperty('--hero-parallax', `${parallax}px`);
      }
    };
    window.addEventListener('scroll', onScroll, { passive: true });
    onScroll();
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  const navigate = useNavigate();
  const apply = () => {
    const token = localStorage.getItem('token');
    navigate(token ? '/dashboard' : '/auth/register');
  };

  return (
    <section id="home" ref={heroRef} className="relative min-h-screen flex items-center overflow-hidden" style={{ background: 'var(--gradient-hero)' }}>
      <div className="absolute inset-0 opacity-20 translate-y-[var(--hero-parallax,0px)]" aria-hidden="true"></div>

      {/* Floating icons */}
      <div ref={addFloatRef} className="hidden md:block absolute top-24 left-10 w-12 h-12 rounded-full bg-white/20 text-white flex items-center justify-center">
        <i className="fas fa-passport"></i>
      </div>
      <div ref={addFloatRef} className="hidden md:block absolute top-40 right-16 w-12 h-12 rounded-full bg-white/20 text-white flex items-center justify-center">
        <i className="fas fa-graduation-cap"></i>
      </div>
      <div ref={addFloatRef} className="hidden md:block absolute bottom-24 left-24 w-12 h-12 rounded-full bg-white/20 text-white flex items-center justify-center">
        <i className="fas fa-briefcase"></i>
      </div>
      <div ref={addFloatRef} className="hidden md:block absolute bottom-16 right-24 w-12 h-12 rounded-full bg-white/20 text-white flex items-center justify-center">
        <i className="fas fa-plane"></i>
      </div>

      <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8 w-full pt-20">
        <div className="grid md:grid-cols-2 gap-8 items-center">
          <div>
            <h1 className="text-4xl md:text-6xl font-extrabold text-white">
              Empowering <span className="gradient-text">Students</span> and <span className="gradient-text">Professionals</span>
            </h1>
            <p className="mt-4 text-white/90 text-lg md:text-xl max-w-prose">
              Scholarships, jobs, and guidance tailored to your journey. Discover opportunities worldwide with expert support.
            </p>
            <div className="mt-8 flex flex-col sm:flex-row gap-3">
              <button onClick={apply} className="btn btn-primary-gradient btn-lg">Apply Now</button>
              <button onClick={() => document.getElementById('services')?.scrollIntoView({ behavior: 'smooth' })} className="btn btn-outline btn-lg text-white border-white hover:bg-white/10">
                Learn More
              </button>
            </div>
          </div>
          <div className="relative h-[360px] md:h-[480px]">
            <div className="absolute inset-0 rounded-full" style={{ background: 'var(--gradient-primary)', filter: 'blur(60px)', opacity: 0.6 }} />
            <div ref={globeRef} className="relative w-72 h-72 md:w-96 md:h-96 rounded-full mx-auto border-4 border-white/20 shadow-2xl" style={{ background: 'radial-gradient(circle at 30% 30%, #4A90E2, #003366)' }} />
          </div>
        </div>
      </div>
    </section>
  );
};

const Section = ({ id, title, children }) => (
  <section id={id} className="py-20">
    <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
      <h2 className="text-3xl md:text-4xl font-bold gradient-text mb-8">{title}</h2>
      {children}
    </div>
  </section>
);

const Services = () => (
  <Section id="services" title="Our Services">
    <div className="grid md:grid-cols-2 gap-6">
      {[{
        icon: 'fa-graduation-cap',
        title: 'Scholarship Opportunities',
        desc: 'Find and apply to scholarships that match your academic profile.'
      }, {
        icon: 'fa-briefcase',
        title: 'Job Opportunities',
        desc: 'Discover global roles and get guidance to land the offer.'
      }].map((s) => (
        <div key={s.title} className="card p-6 hover:shadow-md transition-transform" style={{ transformStyle: 'preserve-3d' }}>
          <div className="w-12 h-12 rounded-lg text-white flex items-center justify-center" style={{ background: 'var(--gradient-primary)' }}>
            <i className={`fas ${s.icon}`}></i>
          </div>
          <h3 className="mt-4 text-xl font-semibold text-primary-700">{s.title}</h3>
          <p className="mt-2 text-gray-600">{s.desc}</p>
          <button onClick={() => {
            const token = localStorage.getItem('token');
            window.location.href = token ? '/dashboard#services' : '/auth/register';
          }} className="mt-4 btn btn-outline">Learn More</button>
        </div>
      ))}
    </div>
  </Section>
);

const Steps = () => (
  <Section id="how-we-help" title="How We Help">
    <div className="grid md:grid-cols-4 gap-4">
      {[
        { n: '01', icon: 'fa-user-plus', title: 'Register', desc: 'Create your free account.' },
        { n: '02', icon: 'fa-file-upload', title: 'Upload Docs', desc: 'Add transcripts, CV, and more.' },
        { n: '03', icon: 'fa-magnifying-glass', title: 'Get Matched', desc: 'We recommend best-fit opportunities.' },
        { n: '04', icon: 'fa-circle-check', title: 'Get Approved', desc: 'Apply and track approvals.' },
      ].map(step => (
        <div key={step.n} className="relative card p-6 hover:shadow-md transition">
          <div className="absolute -top-3 -left-3 text-6xl font-extrabold text-gray-100 select-none">{step.n}</div>
          <div className="w-12 h-12 rounded-full flex items-center justify-center text-white" style={{ background: 'var(--gradient-primary)' }}>
            <i className={`fas ${step.icon}`}></i>
          </div>
          <h3 className="mt-4 font-semibold">{step.title}</h3>
          <p className="text-gray-600">{step.desc}</p>
        </div>
      ))}
    </div>
  </Section>
);

const About = () => (
  <section id="about" className="py-20 text-white" style={{ background: 'var(--gradient-primary)' }}>
    <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8 grid md:grid-cols-2 gap-8 items-center">
      <div>
        <h2 className="text-3xl md:text-4xl font-bold">About Us</h2>
        <p className="mt-3 text-white/90">We connect talent with life-changing opportunities through curated scholarships and jobs, guided by experienced counselors.</p>
      </div>
      <div className="grid grid-cols-3 gap-4">
        {[{ n: '5K+', label: 'Applicants' }, { n: '800+', label: 'Offers' }, { n: '120+', label: 'Partners' }].map((s) => (
          <div key={s.label} className="rounded-lg bg-white/15 p-4 text-center">
            <div className="text-3xl font-extrabold" style={{ color: 'var(--ethiopian-yellow)' }}>{s.n}</div>
            <div className="text-sm opacity-90">{s.label}</div>
          </div>
        ))}
      </div>
    </div>
  </section>
);

const Success = () => (
  <Section id="success" title="Success Stories">
    <div className="max-w-3xl mx-auto">
      <div className="card p-6">
        <div className="text-emerald-600">
          <i className="fas fa-quote-left"></i>
        </div>
        <p className="italic text-gray-700 mt-2">Addis Consult guided me from application to visa. I am now pursuing my master’s in Europe with a full scholarship.</p>
        <div className="mt-4 flex items-center gap-3">
          <img src="https://images.unsplash.com/photo-1527980965255-d3b416303d12?w=80&h=80&fit=crop&crop=faces" alt="Student" className="w-12 h-12 rounded-full object-cover" loading="lazy" />
          <div>
            <div className="font-semibold">Hanna T.</div>
            <div className="text-sm text-gray-500">Master’s Student</div>
          </div>
        </div>
      </div>
    </div>
  </Section>
);

const Partners = () => (
  <Section id="partners" title="Our Partners">
    <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
      {['Addis University', 'TechCo', 'Global Uni', 'SkyHire', 'BlueLabs'].map(p => (
        <div key={p} className="rounded-lg bg-gray-100 p-4 text-center hover:bg-white hover:shadow transition">
          <div className="font-semibold text-primary-700">{p}</div>
        </div>
      ))}
    </div>
  </Section>
);

const FAQ = () => {
  const [open, setOpen] = useState(0);
  const items = [
    { q: 'How do I apply?', a: 'Create an account, complete your profile, and follow the wizard.' },
    { q: 'Is there a service fee?', a: 'Basic use is free. Counseling packages are optional.' },
    { q: 'Do you support jobs abroad?', a: 'Yes, we match candidates with global opportunities.' },
  ];
  return (
    <Section id="faq" title="FAQ">
      <div className="space-y-3">
        {items.map((it, i) => (
          <div key={it.q} className="card">
            <button aria-expanded={open===i} onClick={() => setOpen(open===i?-1:i)} className="w-full flex items-center justify-between p-4 text-left">
              <span className="font-medium text-primary-700">{it.q}</span>
              <i className={`fas fa-chevron-down transition ${open===i ? 'rotate-180' : ''}`}></i>
            </button>
            <div className="px-4 pb-4 text-gray-600" style={{ display: open===i ? 'block' : 'none' }}>{it.a}</div>
          </div>
        ))}
      </div>
    </Section>
  );
};

