const Partners = () => {
  const { lang } = useLang();
  const marqueeRef = useRef(null);
  const trackRef = useRef(null);
  const universities = [
    'University of Oxford',
    'Harvard University',
    'MIT',
    'Stanford University',
    'University of Cambridge',
    'ETH Zürich',
    'University of Toronto',
    'University of Melbourne',
    'KAIST',
    'University of Tokyo',
  ];

  useEffect(() => {
    if (!trackRef.current) return;
    const track = trackRef.current;
    const left = track.children[0];
    let tween;

    const setup = () => {
      if (!left) return;
      const halfWidth = left.scrollWidth;
      // Set explicit width so halves sit side-by-side without overlap
      track.style.width = `${halfWidth * 2}px`;
      gsap.set(track, { x: 0 });
      if (tween) tween.kill();
      const duration = Math.max(20, Math.round(halfWidth / 40)); // speed ~40px/s
      tween = gsap.to(track, { x: -halfWidth, duration, ease: 'none', repeat: -1 });
    };

    setup();
    const onResize = () => setup();
    window.addEventListener('resize', onResize);
    return () => {
      window.removeEventListener('resize', onResize);
      if (tween) tween.kill();
    };
  }, []);

  const Card = ({ name }) => (
    <div className="shrink-0 w-64 mr-4 last:mr-0 rounded-lg bg-gray-100 p-4 text-center hover:bg-white hover:shadow transition">
      <div className="font-semibold text-primary-700 truncate">{name}</div>
      <div className="mt-2 text-xs text-gray-500">Scholarship partner</div>
    </div>
  );

  return (
  <Section id="partners" title={translations[lang].partnersTitle}>
    <div ref={marqueeRef} className="relative overflow-hidden">
      <div ref={trackRef} className="flex items-center will-change-transform">
        <div className="flex items-center py-2">
          {universities.map(u => <Card key={`a-${u}`} name={u} />)}
        </div>
        <div className="flex items-center py-2" aria-hidden="true">
          {universities.map(u => <Card key={`b-${u}`} name={u} />)}
        </div>
      </div>
    </div>
  </Section>
)};

