import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/ui/Card';
import Button from '../../components/ui/Button';
import ProgressBar from '../../components/ui/ProgressBar';
import Badge from '../../components/ui/Badge';
import Modal from '../../components/ui/Modal';
import { useQuery } from 'react-query';
import { applicationsAPI } from '../../services/api';
import toast from 'react-hot-toast';
import {
  FileText,
  Plus,
  Eye,
  Edit,
  Trash2,
  Calendar,
  Briefcase,
  GraduationCap,
  Clock,
  CheckCircle,
  AlertCircle,
  Play,
  RotateCcw,
  Rocket,
  RefreshCw,
} from 'lucide-react';

const ApplicationsPage = () => {
  const navigate = useNavigate();
  const [selectedApplication, setSelectedApplication] = useState(null);
  const [showRestartModal, setShowRestartModal] = useState(false);
  const [restartApplicationId, setRestartApplicationId] = useState(null);

  // Fetch applications from API
  const { data: applicationsData, isLoading, refetch } = useQuery(
    'applications',
    () => applicationsAPI.getApplications(),
    {
      select: (response) => response.data.data?.applications || [],
      onError: (error) => {
        toast.error('Failed to load applications');
        console.error('Error loading applications:', error);
      }
    }
  );

  const applications = applicationsData || [];

  const getStatusIcon = (status) => {
    switch (status) {
      case 'submitted':
        return <CheckCircle className="h-4 w-4 text-green-600" />;
      case 'in_progress':
        return <Clock className="h-4 w-4 text-yellow-600" />;
      case 'draft':
        return <AlertCircle className="h-4 w-4 text-gray-600" />;
      case 'withdrawn':
        return <AlertCircle className="h-4 w-4 text-red-600" />;
      default:
        return <Clock className="h-4 w-4 text-gray-600" />;
    }
  };

  const getStatusBadge = (status) => {
    const variants = {
      submitted: 'success',
      in_progress: 'warning',
      draft: 'secondary',
      withdrawn: 'danger',
    };
    return <Badge variant={variants[status] || 'default'}>{status.replace('_', ' ')}</Badge>;
  };

  const getPaymentStatusBadge = (status) => {
    const variants = {
      paid: 'success',
      unpaid: 'warning',
      pending: 'warning',
      failed: 'danger',
    };
    return <Badge variant={variants[status] || 'default'}>{status}</Badge>;
  };

  const getApplicationTypeIcon = (type) => {
    return type === 'job' ? (
      <Briefcase className="h-5 w-5 text-blue-600" />
    ) : (
      <GraduationCap className="h-5 w-5 text-purple-600" />
    );
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Applications</h1>
          <p className="text-gray-600">
            Track and manage your job and scholarship applications.
          </p>
        </div>
        <div className="flex space-x-3">
          <Button variant="outline" asChild>
            <Link to="/wizard/scholarship">
              <GraduationCap className="mr-2 h-4 w-4" />
              Apply for Scholarship
            </Link>
          </Button>
          <Button asChild>
            <Link to="/wizard/job">
              <Briefcase className="mr-2 h-4 w-4" />
              Apply for Job
            </Link>
          </Button>
        </div>
      </div>

      {/* Applications List */}
      <div className="space-y-4">
        {applications.map((application) => (
          <Card key={application.id} className="hover:shadow-md transition-shadow">
            <CardContent className="p-6">
              <div className="flex items-start justify-between">
                <div className="flex items-start space-x-4">
                  <div className="flex-shrink-0">
                    {getApplicationTypeIcon(application.type)}
                  </div>
                  <div className="flex-1">
                    <div className="flex items-center space-x-2">
                      <h3 className="text-lg font-medium text-gray-900">
                        {application.title}
                      </h3>
                      {getStatusIcon(application.status)}
                      {getStatusBadge(application.status)}
                    </div>
                    <p className="text-sm text-gray-600 mt-1">
                      {application.company || application.university}
                    </p>

                    {/* Progress Bar */}
                    <div className="mt-3">
                      <div className="flex items-center justify-between text-sm text-gray-600 mb-1">
                        <span>Progress</span>
                        <span>{application.progress}%</span>
                      </div>
                      <ProgressBar value={application.progress} size="sm" />
                      <p className="text-xs text-gray-500 mt-1">
                        Step {application.currentStep} of {application.totalSteps}
                      </p>
                    </div>

                    {/* Application Details */}
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mt-4">
                      <div>
                        <label className="text-xs font-medium text-gray-500">Type</label>
                        <p className="text-sm text-gray-900 capitalize">{application.type}</p>
                      </div>
                      <div>
                        <label className="text-xs font-medium text-gray-500">Payment</label>
                        <div className="mt-0.5">
                          {getPaymentStatusBadge(application.paymentStatus)}
                        </div>
                      </div>
                      <div>
                        <label className="text-xs font-medium text-gray-500">Deadline</label>
                        <p className="text-sm text-gray-900">
                          {new Date(application.deadline).toLocaleDateString()}
                        </p>
                      </div>
                      {application.submittedDate && (
                        <div>
                          <label className="text-xs font-medium text-gray-500">Submitted</label>
                          <p className="text-sm text-gray-900">
                            {new Date(application.submittedDate).toLocaleDateString()}
                          </p>
                        </div>
                      )}
                    </div>
                  </div>
                </div>

                {/* Actions */}
                <div className="flex flex-col space-y-2">
                  <div className="flex space-x-2">
                    <Button variant="outline" size="sm">
                      <Eye className="h-4 w-4" />
                    </Button>
                    {application.status !== 'submitted' && (
                      <Button variant="outline" size="sm">
                        <Edit className="h-4 w-4" />
                      </Button>
                    )}
                    <Button variant="outline" size="sm">
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>

                  {application.status === 'in_progress' && (
                    <Button size="sm" className="w-full">
                      Continue Application
                    </Button>
                  )}

                  {application.status === 'draft' && (
                    <Button size="sm" className="w-full">
                      Resume Application
                    </Button>
                  )}
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Empty State */}
      {applications.length === 0 && (
        <Card>
          <CardContent className="p-12 text-center">
            <FileText className="mx-auto h-12 w-12 text-gray-400" />
            <h3 className="mt-2 text-sm font-medium text-gray-900">No applications yet</h3>
            <p className="mt-1 text-sm text-gray-500">
              Get started by applying for a job or scholarship.
            </p>
            <div className="mt-6 flex justify-center space-x-3">
              <Button variant="outline" asChild>
                <Link to="/wizard/scholarship">
                  <GraduationCap className="mr-2 h-4 w-4" />
                  Apply for Scholarship
                </Link>
              </Button>
              <Button asChild>
                <Link to="/wizard/job">
                  <Briefcase className="mr-2 h-4 w-4" />
                  Apply for Job
                </Link>
              </Button>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default ApplicationsPage;
