import axios from 'axios';

// Create axios instance
const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:8000/api',
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle errors
api.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Auth API
export const authAPI = {
  login: (email, password) => api.post('/auth/login/', { email, password }),
  register: (userData) => api.post('/auth/register/', userData),
  logout: () => api.post('/auth/logout/'),
  getProfile: () => api.get('/auth/profile/'),
  updateProfile: (profileData) => api.patch('/auth/profile/', profileData),
  changePassword: (passwordData) => api.post('/auth/change-password/', passwordData),
  forgotPassword: (email) => api.post('/auth/forgot-password/', { email }),
  resetPassword: (token, passwordData) => api.post(`/auth/reset-password/${token}/`, passwordData),
  verifyEmail: (token) => api.post(`/auth/verify-email/${token}/`),
};

// Users API
export const usersAPI = {
  getUsers: (params) => api.get('/users/', { params }),
  getUser: (id) => api.get(`/users/${id}/`),
  createUser: (userData) => api.post('/users/', userData),
  updateUser: (id, userData) => api.patch(`/users/${id}/`, userData),
  deleteUser: (id) => api.delete(`/users/${id}/`),
  getStaff: (params) => api.get('/users/staff/', { params }),
  getApplicants: (params) => api.get('/users/applicants/', { params }),
};

// Opportunities API (Jobs & Scholarships)
export const opportunitiesAPI = {
  getOpportunities: (params) => api.get('/opportunities/', { params }),
  getOpportunity: (id) => api.get(`/opportunities/${id}/`),
  createOpportunity: (opportunityData) => api.post('/opportunities/', opportunityData),
  updateOpportunity: (id, opportunityData) => api.patch(`/opportunities/${id}/`, opportunityData),
  deleteOpportunity: (id) => api.delete(`/opportunities/${id}/`),
  getJobs: (params) => api.get('/opportunities/?type=job', { params }),
  getScholarships: (params) => api.get('/opportunities/?type=scholarship', { params }),
};

// Applications API
export const applicationsAPI = {
  getApplications: (params) => api.get('/applications/', { params }),
  getApplication: (id) => api.get(`/applications/${id}/`),
  createApplication: (applicationData) => api.post('/applications/', applicationData),
  updateApplication: (id, applicationData) => api.patch(`/applications/${id}/`, applicationData),
  deleteApplication: (id) => api.delete(`/applications/${id}/`),
  getUserApplications: (params) => api.get('/applications/my/', { params }),
  submitApplication: (id) => api.post(`/applications/${id}/submit/`),
  withdrawApplication: (id) => api.post(`/applications/${id}/withdraw/`),
};

// Documents API
export const documentsAPI = {
  getDocuments: (params) => api.get('/documents/', { params }),
  getDocument: (id) => api.get(`/documents/${id}/`),
  uploadDocument: (formData) => api.post('/documents/upload/', formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
  }),
  updateDocument: (id, documentData) => api.patch(`/documents/${id}/`, documentData),
  deleteDocument: (id) => api.delete(`/documents/${id}/`),
  downloadDocument: (id) => api.get(`/documents/${id}/download/`, {
    responseType: 'blob',
  }),
};

// Chat API
export const chatAPI = {
  getConversations: (params) => api.get('/chat/conversations/', { params }),
  getConversation: (id) => api.get(`/chat/conversations/${id}/`),
  createConversation: (conversationData) => api.post('/chat/conversations/', conversationData),
  getMessages: (conversationId, params) => api.get(`/chat/conversations/${conversationId}/messages/`, { params }),
  sendMessage: (conversationId, messageData) => api.post(`/chat/conversations/${conversationId}/messages/`, messageData),
  markAsRead: (conversationId, messageIds) => api.post(`/chat/conversations/${conversationId}/mark-read/`, { message_ids: messageIds }),
};

// Payments API
export const paymentsAPI = {
  getPayments: (params) => api.get('/payments/', { params }),
  getPayment: (id) => api.get(`/payments/${id}/`),
  createPayment: (paymentData) => api.post('/payments/', paymentData),
  updatePayment: (id, paymentData) => api.patch(`/payments/${id}/`, paymentData),
  processPayment: (id) => api.post(`/payments/${id}/process/`),
  verifyPayment: (id, verificationData) => api.post(`/payments/${id}/verify/`, verificationData),
};

// Dashboard API
export const dashboardAPI = {
  getDashboardStats: () => api.get('/dashboard/stats/'),
  getRecentActivity: (params) => api.get('/dashboard/activity/', { params }),
  getNotifications: (params) => api.get('/dashboard/notifications/', { params }),
  markNotificationRead: (id) => api.patch(`/dashboard/notifications/${id}/read/`),
  markAllNotificationsRead: () => api.post('/dashboard/notifications/mark-all-read/'),
};

// File upload utility
export const uploadFile = async (file, onProgress) => {
  const formData = new FormData();
  formData.append('file', file);

  return api.post('/documents/upload/', formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
    },
    onUploadProgress: (progressEvent) => {
      if (onProgress) {
        const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
        onProgress(percentCompleted);
      }
    },
  });
};

// Export default api instance
export default api;
