import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useSearchParams } from 'react-router-dom';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/ui/Card';
import Button from '../../components/ui/Button';
import Input from '../../components/ui/Input';
import Textarea from '../../components/ui/Textarea';
import Modal from '../../components/ui/Modal';
import Badge from '../../components/ui/Badge';
import Stepper from '../../components/wizard/Stepper';
import {
  Briefcase,
  GraduationCap,
  MapPin,
  Calendar,
  DollarSign,
  FileText,
  Upload,
  CheckCircle,
  AlertCircle,
  ArrowLeft,
  ArrowRight,
  Save,
  Send,
} from 'lucide-react';

const ApplicationWizard = () => {
  const { type } = useParams();
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const [currentStep, setCurrentStep] = useState(0);
  const [formData, setFormData] = useState({
    applicationType: type,
    selectedJob: null,
    selectedScholarship: null,
    selectedCountry: null,
    selectedProgram: null,
    documents: [],
    personalInfo: {},
    additionalInfo: {},
  });

  // Job application steps
  const jobSteps = [
    { id: 'selection', title: 'Application Selection', description: 'Choose job type' },
    { id: 'country', title: 'Country Selection', description: 'Select target country' },
    { id: 'documents', title: 'Documents & Details', description: 'Upload required documents' },
    { id: 'summary', title: 'Summary & Submission', description: 'Review and submit' },
  ];

  // Scholarship application steps
  const scholarshipSteps = [
    { id: 'selection', title: 'Application Selection', description: 'Choose scholarship type' },
    { id: 'country', title: 'Country Selection', description: 'Select target country' },
    { id: 'program', title: 'Program Selection', description: 'Choose program level' },
    { id: 'scholarship', title: 'Scholarship Selection', description: 'Select specific scholarship' },
    { id: 'documents', title: 'Documents & Details', description: 'Upload required documents' },
    { id: 'summary', title: 'Summary & Submission', description: 'Review and submit' },
  ];

  const steps = type === 'job' ? jobSteps : scholarshipSteps;

  // Mock data - replace with actual API calls
  const countries = [
    { id: 1, name: 'United States', flag: 'ðŸ‡ºðŸ‡¸' },
    { id: 2, name: 'Canada', flag: 'ðŸ‡¨ðŸ‡¦' },
    { id: 3, name: 'United Kingdom', flag: 'ðŸ‡¬ðŸ‡§' },
    { id: 4, name: 'Australia', flag: 'ðŸ‡¦ðŸ‡º' },
    { id: 5, name: 'Germany', flag: 'ðŸ‡©ðŸ‡ª' },
    { id: 6, name: 'France', flag: 'ðŸ‡«ðŸ‡·' },
  ];

  const programs = [
    { id: 1, name: 'Bachelor', description: 'Undergraduate degree programs' },
    { id: 2, name: 'Master\'s', description: 'Graduate degree programs' },
    { id: 3, name: 'PhD', description: 'Doctoral degree programs' },
  ];

  const scholarships = [
    {
      id: 1,
      name: 'Harvard MBA Scholarship',
      university: 'Harvard Business School',
      country: 'United States',
      program: 'Master\'s',
      fee: 0,
      deadline: '2024-03-15',
    },
    {
      id: 2,
      name: 'MIT PhD Fellowship',
      university: 'MIT',
      country: 'United States',
      program: 'PhD',
      fee: 0,
      deadline: '2024-02-28',
    },
    {
      id: 3,
      name: 'Oxford Masters Scholarship',
      university: 'University of Oxford',
      country: 'United Kingdom',
      program: 'Master\'s',
      fee: 5000,
      deadline: '2024-04-01',
    },
  ];

  const jobs = [
    {
      id: 1,
      name: 'Software Engineer',
      company: 'Google',
      country: 'United States',
      fee: 0,
      deadline: '2024-03-15',
    },
    {
      id: 2,
      name: 'Data Scientist',
      company: 'Microsoft',
      country: 'Canada',
      fee: 50,
      deadline: '2024-02-28',
    },
  ];

  useEffect(() => {
    // Pre-select job/scholarship if provided in URL
    const jobId = searchParams.get('jobId');
    const scholarshipId = searchParams.get('scholarshipId');

    if (jobId) {
      const job = jobs.find(j => j.id === parseInt(jobId));
      if (job) {
        setFormData(prev => ({ ...prev, selectedJob: job }));
        setCurrentStep(1); // Skip to country selection
      }
    }

    if (scholarshipId) {
      const scholarship = scholarships.find(s => s.id === parseInt(scholarshipId));
      if (scholarship) {
        setFormData(prev => ({ ...prev, selectedScholarship: scholarship }));
        setCurrentStep(1); // Skip to country selection
      }
    }
  }, [searchParams]);

  const handleNext = () => {
    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const handlePrevious = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleStepClick = (stepIndex) => {
    setCurrentStep(stepIndex);
  };

  const handleSaveDraft = () => {
    // Save draft logic
    console.log('Saving draft:', formData);
    navigate('/applications');
  };

  const handleSubmit = () => {
    // Submit application logic
    console.log('Submitting application:', formData);
    navigate('/applications');
  };

  const renderStepContent = () => {
    const stepId = steps[currentStep].id;

    switch (stepId) {
      case 'selection':
        return (
          <div className="space-y-6">
            <div className="text-center">
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                Choose Application Type
              </h3>
              <p className="text-sm text-gray-600">
                Select the type of application you want to create
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <Card 
                className={`cursor-pointer transition-all ${
                  formData.applicationType === 'job' ? 'ring-2 ring-primary-500' : 'hover:shadow-md'
                }`}
                onClick={() => setFormData(prev => ({ ...prev, applicationType: 'job' }))}
              >
                <CardContent className="p-6 text-center">
                  <Briefcase className="h-12 w-12 text-blue-600 mx-auto mb-4" />
                  <h4 className="font-medium text-gray-900">Job Application</h4>
                  <p className="text-sm text-gray-600 mt-2">
                    Apply for job opportunities around the world
                  </p>
                </CardContent>
              </Card>

              <Card 
                className={`cursor-pointer transition-all ${
                  formData.applicationType === 'scholarship' ? 'ring-2 ring-primary-500' : 'hover:shadow-md'
                }`}
                onClick={() => setFormData(prev => ({ ...prev, applicationType: 'scholarship' }))}
              >
                <CardContent className="p-6 text-center">
                  <GraduationCap className="h-12 w-12 text-purple-600 mx-auto mb-4" />
                  <h4 className="font-medium text-gray-900">Scholarship Application</h4>
                  <p className="text-sm text-gray-600 mt-2">
                    Apply for scholarship opportunities
                  </p>
                </CardContent>
              </Card>
            </div>
          </div>
        );

      case 'country':
        return (
          <div className="space-y-6">
            <div className="text-center">
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                Select Target Country
              </h3>
              <p className="text-sm text-gray-600">
                Choose the country where you want to work or study
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {countries.map((country) => (
                <Card
                  key={country.id}
                  className={`cursor-pointer transition-all ${
                    formData.selectedCountry?.id === country.id ? 'ring-2 ring-primary-500' : 'hover:shadow-md'
                  }`}
                  onClick={() => setFormData(prev => ({ ...prev, selectedCountry: country }))}
                >
                  <CardContent className="p-4 text-center">
                    <div className="text-2xl mb-2">{country.flag}</div>
                    <h4 className="font-medium text-gray-900">{country.name}</h4>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>
        );

      case 'program':
        return (
          <div className="space-y-6">
            <div className="text-center">
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                Select Program Level
              </h3>
              <p className="text-sm text-gray-600">
                Choose the program level you want to apply for
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              {programs.map((program) => (
                <Card
                  key={program.id}
                  className={`cursor-pointer transition-all ${
                    formData.selectedProgram?.id === program.id ? 'ring-2 ring-primary-500' : 'hover:shadow-md'
                  }`}
                  onClick={() => setFormData(prev => ({ ...prev, selectedProgram: program }))}
                >
                  <CardContent className="p-6 text-center">
                    <GraduationCap className="h-12 w-12 text-purple-600 mx-auto mb-4" />
                    <h4 className="font-medium text-gray-900">{program.name}</h4>
                    <p className="text-sm text-gray-600 mt-2">{program.description}</p>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>
        );

      case 'scholarship':
        return (
          <div className="space-y-6">
            <div className="text-center">
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                Select Scholarship
              </h3>
              <p className="text-sm text-gray-600">
                Choose the specific scholarship you want to apply for
              </p>
            </div>

            <div className="space-y-4">
              {scholarships.map((scholarship) => (
                <Card
                  key={scholarship.id}
                  className={`cursor-pointer transition-all ${
                    formData.selectedScholarship?.id === scholarship.id ? 'ring-2 ring-primary-500' : 'hover:shadow-md'
                  }`}
                  onClick={() => setFormData(prev => ({ ...prev, selectedScholarship: scholarship }))}
                >
                  <CardContent className="p-4">
                    <div className="flex justify-between items-start">
                      <div>
                        <h4 className="font-medium text-gray-900">{scholarship.name}</h4>
                        <p className="text-sm text-gray-600">{scholarship.university}</p>
                        <div className="flex items-center space-x-4 mt-2 text-xs text-gray-500">
                          <span className="flex items-center">
                            <MapPin className="h-3 w-3 mr-1" />
                            {scholarship.country}
                          </span>
                          <span className="flex items-center">
                            <GraduationCap className="h-3 w-3 mr-1" />
                            {scholarship.program}
                          </span>
                          <span className="flex items-center">
                            <DollarSign className="h-3 w-3 mr-1" />
                            {scholarship.fee === 0 ? 'Free' : `$${scholarship.fee}`}
                          </span>
                        </div>
                      </div>
                      <div className="text-right">
                        <p className="text-xs text-gray-500">
                          Deadline: {new Date(scholarship.deadline).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </div>
        );

      case 'documents':
        return (
          <div className="space-y-6">
            <div className="text-center">
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                Documents & Additional Information
              </h3>
              <p className="text-sm text-gray-600">
                Upload required documents and provide additional information
              </p>
            </div>

            <div className="space-y-6">
              {/* Document Upload */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-3">
                  Required Documents
                </label>
                <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
                  <Upload className="mx-auto h-12 w-12 text-gray-400" />
                  <div className="mt-4">
                    <label htmlFor="file-upload" className="cursor-pointer">
                      <span className="mt-2 block text-sm font-medium text-gray-900">
                        Upload documents
                      </span>
                      <span className="mt-1 block text-sm text-gray-500">
                        PDF, DOC, DOCX, PNG, JPG (MAX. 10MB each)
                      </span>
                    </label>
                    <input
                      id="file-upload"
                      name="file-upload"
                      type="file"
                      multiple
                      className="sr-only"
                      accept=".pdf,.doc,.docx,.png,.jpg,.jpeg"
                    />
                  </div>
                </div>
              </div>

              {/* Additional Information */}
              <div>
                <label htmlFor="additional-info" className="block text-sm font-medium text-gray-700 mb-2">
                  Additional Information
                </label>
                <Textarea
                  id="additional-info"
                  rows={4}
                  placeholder="Provide any additional information that might be relevant to your application..."
                  value={formData.additionalInfo.notes || ''}
                  onChange={(e) => setFormData(prev => ({
                    ...prev,
                    additionalInfo: { ...prev.additionalInfo, notes: e.target.value }
                  }))}
                />
              </div>

              {/* Job-specific fields */}
              {formData.applicationType === 'job' && (
                <div>
                  <label htmlFor="job-interest" className="block text-sm font-medium text-gray-700 mb-2">
                    Why are you interested in this position?
                  </label>
                  <Textarea
                    id="job-interest"
                    rows={3}
                    placeholder="Explain your interest in this position..."
                    value={formData.additionalInfo.jobInterest || ''}
                    onChange={(e) => setFormData(prev => ({
                      ...prev,
                      additionalInfo: { ...prev.additionalInfo, jobInterest: e.target.value }
                    }))}
                  />
                </div>
              )}

              {/* Scholarship-specific fields */}
              {formData.applicationType === 'scholarship' && (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label htmlFor="major" className="block text-sm font-medium text-gray-700 mb-2">
                      Intended Major
                    </label>
                    <select
                      id="major"
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary-500"
                      value={formData.additionalInfo.major || ''}
                      onChange={(e) => setFormData(prev => ({
                        ...prev,
                        additionalInfo: { ...prev.additionalInfo, major: e.target.value }
                      }))}
                    >
                      <option value="">Select major</option>
                      <option value="Computer Science">Computer Science</option>
                      <option value="Business Administration">Business Administration</option>
                      <option value="Engineering">Engineering</option>
                      <option value="Medicine">Medicine</option>
                      <option value="Law">Law</option>
                    </select>
                  </div>
                  <div>
                    <label htmlFor="passport" className="block text-sm font-medium text-gray-700 mb-2">
                      Passport Number
                    </label>
                    <Input
                      id="passport"
                      placeholder="Enter passport number"
                      value={formData.additionalInfo.passportNumber || ''}
                      onChange={(e) => setFormData(prev => ({
                        ...prev,
                        additionalInfo: { ...prev.additionalInfo, passportNumber: e.target.value }
                      }))}
                    />
                  </div>
                </div>
              )}
            </div>
          </div>
        );

      case 'summary':
        return (
          <div className="space-y-6">
            <div className="text-center">
              <h3 className="text-lg font-medium text-gray-900 mb-2">
                Review Your Application
              </h3>
              <p className="text-sm text-gray-600">
                Please review all information before submitting your application
              </p>
            </div>

            <div className="bg-gray-50 rounded-lg p-6 space-y-4">
              <div>
                <h4 className="font-medium text-gray-900 mb-3">Application Details</h4>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                  <div>
                    <span className="font-medium text-gray-700">Type:</span>
                    <span className="ml-2 text-gray-900 capitalize">
                      {formData.applicationType}
                    </span>
                  </div>
                  <div>
                    <span className="font-medium text-gray-700">Country:</span>
                    <span className="ml-2 text-gray-900">
                      {formData.selectedCountry?.name}
                    </span>
                  </div>
                  {formData.selectedProgram && (
                    <div>
                      <span className="font-medium text-gray-700">Program:</span>
                      <span className="ml-2 text-gray-900">{formData.selectedProgram.name}</span>
                    </div>
                  )}
                  {formData.selectedScholarship && (
                    <div>
                      <span className="font-medium text-gray-700">Scholarship:</span>
                      <span className="ml-2 text-gray-900">{formData.selectedScholarship.name}</span>
                    </div>
                  )}
                  {formData.selectedJob && (
                    <div>
                      <span className="font-medium text-gray-700">Job:</span>
                      <span className="ml-2 text-gray-900">{formData.selectedJob.name}</span>
                    </div>
                  )}
                </div>
              </div>

              {formData.additionalInfo.notes && (
                <div>
                  <h4 className="font-medium text-gray-900 mb-2">Additional Information</h4>
                  <p className="text-sm text-gray-600">{formData.additionalInfo.notes}</p>
                </div>
              )}

              <div>
                <h4 className="font-medium text-gray-900 mb-3">Required Documents</h4>
                <div className="space-y-2">
                  <div className="flex items-center text-sm">
                    <CheckCircle className="h-4 w-4 text-green-600 mr-2" />
                    <span>Resume/CV</span>
                  </div>
                  <div className="flex items-center text-sm">
                    <CheckCircle className="h-4 w-4 text-green-600 mr-2" />
                    <span>Academic Transcripts</span>
                  </div>
                  <div className="flex items-center text-sm">
                    <CheckCircle className="h-4 w-4 text-green-600 mr-2" />
                    <span>Cover Letter</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  const canProceed = () => {
    const stepId = steps[currentStep].id;

    switch (stepId) {
      case 'selection':
        return formData.applicationType;
      case 'country':
        return formData.selectedCountry;
      case 'program':
        return formData.selectedProgram;
      case 'scholarship':
        return formData.selectedScholarship;
      case 'documents':
        return true; // Documents are optional
      case 'summary':
        return true;
      default:
        return false;
    }
  };

  return (
    <div className="max-w-4xl mx-auto py-8 px-4">
      {/* Header */}
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold text-gray-900">
          {type === 'job' ? 'Job Application' : 'Scholarship Application'}
        </h1>
        <p className="text-gray-600 mt-2">
          Complete the steps below to submit your application
        </p>
      </div>

      {/* Stepper */}
      <Stepper
        steps={steps}
        currentStep={currentStep}
        onStepClick={handleStepClick}
      />

      {/* Step Content */}
      <Card>
        <CardContent className="p-8">
          {renderStepContent()}
        </CardContent>
      </Card>

      {/* Navigation */}
      <div className="flex justify-between mt-8">
        <Button
          variant="outline"
          onClick={handlePrevious}
          disabled={currentStep === 0}
        >
          <ArrowLeft className="mr-2 h-4 w-4" />
          Previous
        </Button>

        <div className="flex space-x-3">
          <Button variant="outline" onClick={handleSaveDraft}>
            <Save className="mr-2 h-4 w-4" />
            Save Draft
          </Button>

          {currentStep < steps.length - 1 ? (
            <Button onClick={handleNext} disabled={!canProceed()}>
              Next
              <ArrowRight className="ml-2 h-4 w-4" />
            </Button>
          ) : (
            <Button onClick={handleSubmit} disabled={!canProceed()}>
              <Send className="mr-2 h-4 w-4" />
              Submit Application
            </Button>
          )}
        </div>
      </div>
    </div>
  );
};

export default ApplicationWizard;
