import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { useAuth } from '../../contexts/AuthContext';
import Button from '../../components/ui/Button';
import Input from '../../components/ui/Input';
import { Eye, EyeOff } from 'lucide-react';

const Register = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const navigate = useNavigate();
  const { register: registerUser, loading, error, clearError } = useAuth();

  const {
    register,
    handleSubmit,
    watch,
    formState: { errors },
  } = useForm();

  const password = watch('password');

  const onSubmit = async (data) => {
    try {
      await registerUser(data);
      navigate('/dashboard');
    } catch (error) {
      // Error is handled by AuthContext
    }
  };

  return (
    <div className="space-y-6">
      <div>
        <h2 className="text-2xl font-bold text-gray-900">Create your account</h2>
        <p className="mt-2 text-sm text-gray-600">
          Or{' '}
          <Link
            to="/login"
            className="font-medium text-primary-600 hover:text-primary-500"
          >
            sign in to your existing account
          </Link>
        </p>
      </div>

      <form className="space-y-6" onSubmit={handleSubmit(onSubmit)}>
        {error && (
          <div className="rounded-md bg-danger-50 p-4">
            <div className="text-sm text-danger-700">{error}</div>
            <button
              type="button"
              onClick={clearError}
              className="mt-2 text-sm text-danger-600 hover:text-danger-500"
            >
              Dismiss
            </button>
          </div>
        )}

        <div className="grid grid-cols-1 gap-6 sm:grid-cols-3">
          <div>
            <label htmlFor="first_name" className="block text-sm font-medium text-gray-700">
              First name
            </label>
            <div className="mt-1">
              <Input
                id="first_name"
                type="text"
                autoComplete="given-name"
                placeholder="Enter your first name"
                {...register('first_name', {
                  required: 'First name is required',
                  minLength: {
                    value: 2,
                    message: 'First name must be at least 2 characters',
                  },
                })}
              />
              {errors.first_name && (
                <p className="mt-1 text-sm text-danger-600">{errors.first_name.message}</p>
              )}
            </div>
          </div>

          <div>
            <label htmlFor="father_name" className="block text-sm font-medium text-gray-700">
              Father name
            </label>
            <div className="mt-1">
              <Input
                id="father_name"
                type="text"
                placeholder="Enter your father's name"
                {...register('father_name', {
                  required: 'Father name is required',
                  minLength: {
                    value: 2,
                    message: 'Father name must be at least 2 characters',
                  },
                })}
              />
              {errors.father_name && (
                <p className="mt-1 text-sm text-danger-600">{errors.father_name.message}</p>
              )}
            </div>
          </div>

          <div>
            <label htmlFor="grandfather_name" className="block text-sm font-medium text-gray-700">
              Grandfather name
            </label>
            <div className="mt-1">
              <Input
                id="grandfather_name"
                type="text"
                placeholder="Enter your grandfather's name"
                {...register('grandfather_name', {
                  required: 'Grandfather name is required',
                  minLength: {
                    value: 2,
                    message: 'Grandfather name must be at least 2 characters',
                  },
                })}
              />
              {errors.grandfather_name && (
                <p className="mt-1 text-sm text-danger-600">{errors.grandfather_name.message}</p>
              )}
            </div>
          </div>
        </div>

        <div>
          <label htmlFor="email" className="block text-sm font-medium text-gray-700">
            Email address
          </label>
          <div className="mt-1">
            <Input
              id="email"
              type="email"
              autoComplete="email"
              placeholder="Enter your email"
              {...register('email', {
                required: 'Email is required',
                pattern: {
                  value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                  message: 'Invalid email address',
                },
              })}
            />
            {errors.email && (
              <p className="mt-1 text-sm text-danger-600">{errors.email.message}</p>
            )}
          </div>
        </div>

        <div>
          <label htmlFor="username" className="block text-sm font-medium text-gray-700">
            Username
          </label>
          <div className="mt-1">
            <Input
              id="username"
              type="text"
              autoComplete="username"
              placeholder="Choose a username"
              {...register('username', {
                required: 'Username is required',
                minLength: {
                  value: 3,
                  message: 'Username must be at least 3 characters',
                },
                pattern: {
                  value: /^[a-zA-Z0-9_]+$/,
                  message: 'Username can only contain letters, numbers, and underscores',
                },
              })}
            />
            {errors.username && (
              <p className="mt-1 text-sm text-danger-600">{errors.username.message}</p>
            )}
          </div>
        </div>

        <div>
          <label htmlFor="phone" className="block text-sm font-medium text-gray-700">
            Phone number
          </label>
          <div className="mt-1">
            <Input
              id="phone"
              type="tel"
              autoComplete="tel"
              placeholder="Enter your phone number"
              {...register('phone', {
                pattern: {
                  value: /^[\+]?[1-9][\d]{0,15}$/,
                  message: 'Invalid phone number',
                },
              })}
            />
            {errors.phone && (
              <p className="mt-1 text-sm text-danger-600">{errors.phone.message}</p>
            )}
          </div>
        </div>

        <div>
          <label htmlFor="national_id" className="block text-sm font-medium text-gray-700">
            National ID (Optional)
          </label>
          <div className="mt-1">
            <Input
              id="national_id"
              type="text"
              placeholder="Enter your national ID"
              {...register('national_id', {
                minLength: {
                  value: 5,
                  message: 'National ID must be at least 5 characters',
                },
                maxLength: {
                  value: 50,
                  message: 'National ID must be less than 50 characters',
                },
              })}
            />
            {errors.national_id && (
              <p className="mt-1 text-sm text-danger-600">{errors.national_id.message}</p>
            )}
          </div>
        </div>

        <div>
          <label htmlFor="password" className="block text-sm font-medium text-gray-700">
            Password
          </label>
          <div className="mt-1 relative">
            <Input
              id="password"
              type={showPassword ? 'text' : 'password'}
              autoComplete="new-password"
              placeholder="Create a password"
              {...register('password', {
                required: 'Password is required',
                minLength: {
                  value: 6,
                  message: 'Password must be at least 6 characters',
                },
                pattern: {
                  value: /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/,
                  message: 'Password must contain at least one uppercase letter, one lowercase letter, and one number',
                },
              })}
            />
            <button
              type="button"
              className="absolute inset-y-0 right-0 pr-3 flex items-center"
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? (
                <EyeOff className="h-4 w-4 text-gray-400" />
              ) : (
                <Eye className="h-4 w-4 text-gray-400" />
              )}
            </button>
            {errors.password && (
              <p className="mt-1 text-sm text-danger-600">{errors.password.message}</p>
            )}
          </div>
        </div>

        <div>
          <label htmlFor="confirm_password" className="block text-sm font-medium text-gray-700">
            Confirm password
          </label>
          <div className="mt-1 relative">
            <Input
              id="confirm_password"
              type={showConfirmPassword ? 'text' : 'password'}
              autoComplete="new-password"
              placeholder="Confirm your password"
              {...register('confirm_password', {
                required: 'Please confirm your password',
                validate: (value) =>
                  value === password || 'Passwords do not match',
              })}
            />
            <button
              type="button"
              className="absolute inset-y-0 right-0 pr-3 flex items-center"
              onClick={() => setShowConfirmPassword(!showConfirmPassword)}
            >
              {showConfirmPassword ? (
                <EyeOff className="h-4 w-4 text-gray-400" />
              ) : (
                <Eye className="h-4 w-4 text-gray-400" />
              )}
            </button>
            {errors.confirm_password && (
              <p className="mt-1 text-sm text-danger-600">{errors.confirm_password.message}</p>
            )}
          </div>
        </div>

        <div className="flex items-center">
          <input
            id="agree-terms"
            name="agree-terms"
            type="checkbox"
            className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
            {...register('agree_terms', {
              required: 'You must agree to the terms and conditions',
            })}
          />
          <label htmlFor="agree-terms" className="ml-2 block text-sm text-gray-900">
            I agree to the{' '}
            <a href="#" className="text-primary-600 hover:text-primary-500">
              Terms and Conditions
            </a>{' '}
            and{' '}
            <a href="#" className="text-primary-600 hover:text-primary-500">
              Privacy Policy
            </a>
          </label>
        </div>
        {errors.agree_terms && (
          <p className="text-sm text-danger-600">{errors.agree_terms.message}</p>
        )}

        <div>
          <Button
            type="submit"
            className="w-full"
            loading={loading}
            disabled={loading}
          >
            Create account
          </Button>
        </div>
      </form>
    </div>
  );
};

export default Register;
